import React from "react";
import Title from "../Title/Title";
import { useState , useEffect} from "react";
import './History.css';

const History = (props)=>{
    console.log(props.takenform , "adalsdkjfasdfasdfadsfdf");
   const dummy = [
    {
        title : "Cash" , 
        money : 500 , 
        sign : 1 , // 1 or zero
    },
    {
        title : "Book" , 
        money : 500 , 
        sign : 0 , // 1 or zero
    }
   ]
   const [historyArry , sethistory]=useState(dummy);
//    sethistory(props.takenform);
const store = historyArry.map((ele)=>{
    if(ele.sign===1){
        return <div className="pos"><div>{ele.title}</div> <div> +{ele.money} </div></div>
    }
    else return <div className="neg"><div>{ele.title}</div> <div> {-1*ele.money} </div></div>
})
useEffect(()=>{
 sethistory(props.takenform);
} , [props.takenform]);

   return (
      <div className="history">
        <Title value="History"/>
        <div className="store">
           {store}
        </div>
      </div>
   );
}

export default History;
