import React from "react";
import Title from "../Title/Title";
import { useState } from "react";
import './History.css';

const History = ()=>{
   const dummy = [
    {
        title : "Cash" , 
        money : 500 , 
        sign : 1 , // 1 or zero
    },
    {
        title : "Book" , 
        money : 500 , 
        sign : 0 , // 1 or zero
    }
   ]
   const [historyArry , sethistory]=useState(dummy);
   const store = historyArry.map((ele)=>{
    if(ele.sign==1){
        return <div className="pos">{ele.title} {ele.money}</div>
    }
    else return <div className="neg">{ele.title} {-1*ele.money}</div>
   })

   return (
      <div className="history">
        <Title value="History"/>
        <div>
           {store}
        </div>
      </div>
   );
}

export default History;
