import React, { useState , useContext } from "react";
import './TransactionForm.css'
import Button from "../Button/Button";
import Title from "../Title/Title";
import NoteContext from "../context/NoteContext";
const TransactionForm = (props)=>{
    const [newTitle , setnewTitle]=useState("Enter text...");
    const [newAmount , setnewAmount] = useState("Enter amount...");

    // const->
    const titleHandler =  (event)=>{
        setnewTitle(event.target.value);
    }

    const amountHandler = (event)=>{
        setnewAmount(event.target.value);
    }

    const check=useContext(NoteContext);
    const submitHandler=(event)=>{
        event.preventDefault();
        const flag=1;
        const a = parseInt(newAmount);
        if(a<0) flag=0;
        a=Math.abs(a);
        const addDetail = {
            title : newTitle ,
            money : a,
            sign : flag
        }
        check.historyHandler(addDetail);
        // props.getdataform(addDetail);
        setnewAmount("Enter amount...");
        setnewTitle("Enter title...");
    }
    return (
    <div>
       <form onSubmit={submitHandler} className="formDetail">
           <Title value={"Add new transaction"}/>
           
           <label>Text</label> 
           <input onChange={titleHandler} className="input" type="text" placeholder={newTitle} />
           <label>Amount 
                <br/>
                {`(negative-expense , positive-income)`}
           </label>
           <input onChange={amountHandler} className="input" type="number" placeholder={newAmount}/>
           <Button type="submit" buttonValue="Add transaction"></Button>
       </form>

    </div>
    );
}

export default TransactionForm ;
