import { createContext , useState } from "react";

const NoteContext = createContext();

export const NoteState = (props) =>{
    //
    const dummy = [
        {
            title : "Cash" , 
            money : 500 , 
            sign : 1 , // 1 or zero
        },
        {
            title : "Book" , 
            money : 500 , 
            sign : 0 , // 1 or zero
        }
       ]

    return (
        <NoteContext.Provider value={{dummy}}>
            {props.children}
        </NoteContext.Provider>
    )
}

export default NoteContext;
