import { createContext , useState } from "react";

const NoteContext = createContext();

export const NoteState = (props) =>{
    //
    const [histArray , sethistArray] = useState({});
    const dummy = [
        {
            title : "Cash" , 
            money : 500 , 
            sign : 1 , // 1 or zero
        },
        {
            title : "Book" , 
            money : 500 , 
            sign : 0 , // 1 or zero
        }
       ]
       const historyHandler = (added)=>{
        sethistArray((prev)=>{
            return [...prev , added];
        })
       }
    //    console.log("kjhkj");
    return (
        <NoteContext.Provider value={{dummy , historyHandler}}>
            {props.children}
        </NoteContext.Provider>
    )
}

export default NoteContext;
