import React, { useEffect, useState } from "react";

export const History = (props)=>{
   const [arr , setarr]=useState([]);
   useEffect(()=>{
      setarr((prev)=>{
        return [...prev , props.va]
      })
   },[props.value])
   const store = arr.map((mp)=>{
    return <div>{mp}</div>
   })
   return (
    <div>
        <h1>History</h1>
        {store}
    </div>
   )
}
