import React, { Fragment, useContext } from "react";
import './YourCart.css'
import AddedItems from "./AddedItems";
import { GlobalContext } from "../context/GlobalState";
export const YourCart = ()=>{
  const context = useContext(GlobalContext);
  const totalAmount = context.AddItem.map((item)=>item.amount);
  const amount = totalAmount.reduce((acc , amt) => acc=acc+Number(amt) , 0);

  const clickHandler = ()=>{
    context.setvalid(false);
  }
    return (
    <div className="big_container">
      <AddedItems/>

      {/*  */}
      <div className="total">
        <div className="tot">Total Amount</div>
        <div className="totamt">${amount}</div>
      </div>
      <div className="clk">
        <button className="order">order</button>
        <button onClick={clickHandler} className="close">close</button>
      </div>
    </div>
    );
}
