import React , {createContext , useReducer, useState} from "react";

// AddItem

const AddItems = (state , action)=>{
    switch (action.type){
        case 'ADD_ORDER':
            return {
                ... state , 
                AddItem : [...state.AddItem , action.order]
            }
        // case 'NEW' :{
        //     return action.newIn
        // }
        default : 
         return state;
    }
}

// initial state

const initialState = {
    AddItem : [
        {id:1 , name:"Halva" , amount:45 , val:1},
        {id:2 , name:"Chole bhature" ,amount:55 , val:1}
    ]
}

// create global context

export const GlobalContext = createContext(initialState);
// provider

export const GlobalProvider = (props)=>{
    const [state , dispatch] = useReducer(AddItems , initialState);
    const [valid , setvalid] = useState(false);
    const [updateVal , setupdateVal] = useState();
    function addItemCart(trans){
        dispatch({
            type:'ADD_ORDER' , 
            order : trans
        })
    }
    // const validity = ()=>{
    //      setvalid(true);
    // }
    const newValue = (getid)=>{
        console.log("getid = > " , getid);
        initialState.AddItem.map((item)=>{
           if(item.id==Number(getid)){
                item.val=item.val+1;
                console.log(item.val , "checking asdlfj");
            }
            return item;
        })
        console.log(initialState , "newState");
        // dispatch({
        //     type:'NEW' , 
        //     newIn : initialState
        // })
    }

    return (
        <GlobalContext.Provider value={{AddItem : state.AddItem , addItemCart , setvalid , valid , newValue} }>

            {props.children};
        </GlobalContext.Provider>
    )
}
