import React , {createContext , useReducer} from "react";

// AddItem

const AddItems = (state , action)=>{
    switch (action.type){
        case 'ADD_ORDER':
            return {
                // ... state , 
                AddItem : [...state.AddItem , action.order]
            }
        default : 
         return state;
    }
}

// initial state

const initialState = {
    AddItem : [
        {id:1 , name:"Halva" , amount:45 , val:1},
        {id:2 , name:"Chole bhature" ,amount:55 , val:1}
    ]
}

// create global context

export const GlobalContext = createContext(initialState);
// provider

export const GlobalProvider = (props)=>{
    const [state , dispatch] = useReducer(AddItems , initialState);

    function addItemCart(trans){
        dispatch({
            type:'ADD_ORDER' , 
            order : trans
        })
    }

    return (
        <GlobalContext.Provider value={{AddItem : state.AddItem , addItemCart} }>

            {props.children};
        </GlobalContext.Provider>
    )
}
