import React from "react";
import { GlobalContext } from "../context/GlobalState";
import { useContext } from "react";
import './YourCart.css'
const AddedItems = ()=>{
    const context = useContext(GlobalContext);
    const a = context.AddItem;
    const addItemHandler = (event)=>{
      console.log(event.target.val , " check if it is working ");
    }
    // console.log("check kr rha hu " , a);
    const array = a.map((add)=>(
       
        <div className="top">
        <div className="header">
           <div className="dishname">{add.name}</div>
           <div className="quantity">
               <div className="amt">${add.amount}</div>
               <div className="quan">x{add.val}</div>
           </div>
        </div>
        <div className="sign">
           <div className="neg">-</div>
           <div onClick={addItemHandler} className="pos">+</div>
        </div>
       </div>
        )
     )


     return (
        <div>
        {array}
            </div>
     );
}

export default AddedItems;
