import React , {useReducer} from "react";
import './form.css'
import Button from "../button/Button";

const nameReducer = (state , action)=>{
     if(action.type ==='USER_INPUT'){
         return {value : action.val , isValid : action.val.trim().includes('')}
     }
}

const Form = ()=>{
   // i can use this 
    // useReducer
    const [nameState , dispatchName] = useReducer(nameReducer , {
        value: ' ' , isValid : true,
    })
    const nameHandler = (event)=>{
        dispatchName({type:'USER_INPUT' , val : event.target.value}); // passing a object
    }
    const submitHandler = (event)=>{
        event.preventDefault();
    }

    return (
        <div>
          <form onSubmit={submitHandler} className="Form_container">
            <label>UserName</label>
            <input onChange={nameHandler} type="text"></input>
            <label>Password</label>
            <input type="password"></input>
            <Button type="submit" buttonValue="Login" ></Button>
          </form>
        </div>
    );
}

export default Form;
