import { createContext } from "react";

const NoteContext = createContext();

export const NoteState = (props) =>{
    const state = {
        name : "parag",
        age : 18 
    }
    

    return (
        <NoteContext.Provider value={state}>
             {props.children}
        </NoteContext.Provider>
    );
}

export default NoteContext;
