import Expense from "./Expense";
import NewExpense from "./NewExpense/NewExpense";
import './app.css'
import React , {useState} from "react";

    function App() {
        const [newDate , setnewDate] = useState(new Date(2020 ,  5 , 9));
        const [newTitle , setnewTitle] = useState('Titles');
        const [newAmount , setnewAmount] = useState('0.00');

        const items = [
          {
              date : newDate  ,
              title : newTitle ,
              amount : newAmount
          } ,
      
          {
              date : new Date(2003 , 6 , 10) , 
              title : "life Insurance" , 
              amount : 300.99
          } ,
      
          {
              date : new Date(2006 , 11 , 21) , 
              title : "Scooty Insurance" , 
              amount : 999.09
          } ,
          {
              date: new Date(2012 , 10 , 12) , 
              title : "Maggi Insurance" , 
              amount : 123.45
          }
      
        ];

        const newItemsValue = (expenseDataWithNewValue)=>{
              const newItemsdata = {
                ...expenseDataWithNewValue 
              }
              setnewDate(newItemsdata["date"]);
              setnewTitle(newItemsdata["titles"]);
              setnewAmount(newItemsdata["amount"]);
              
              console.log("reached in app.js" , newItemsdata["date"].getMonth() ," -> " , newItemsdata["titles"]);
              console.log(newAmount , newTitle);
        }

        return (
          <div className="page" >
            {/* <h2>Let's get started!</h2> */}
            <NewExpense onNewdata={newItemsValue}></NewExpense>
            <div className="Bigcontainer">
                <Expense a = {items}></Expense>
            </div>
          </div>
        );
}

export default App;
