import React , {useState} from "react";
import './input.css';

const Input = (props) =>{
     const[newName , setnewName]=useState(props.ValueName);//
     const[newAge  , setnewAge]=useState(props.ValueAge);  //
     
      if(props.isSubmit){
        setnewAge('');
        setnewName('');
        
      }
    //   setnewAge(props.ValueAge);
    //   setnewName(props.ValueName);

      const nameHandler = (event) =>{
          setnewName(event.target.value);
          props.newNametake(event.target.value);
      }
      const ageHandler = (event)=>{
          setnewAge(event.target.value);
          props.newAgetake(event.target.value);
      }

      return (
         
         <div className="detailFlex">
             <label className="Details">Username</label>
             <input className="EnterDetail" type="text" onChange={nameHandler} value={newName} ></input>
             <label className="Details">Age {`(Years)`}</label>
             <input className="EnterDetail" type="Number" onChange={ageHandler} value={newAge} ></input>
             
         </div>
         
      );
}

export default Input;
