import React , {useState} from "react";
import './input.css';

const Input = (props) =>{
     const [newdatail , setnewDetail]=useState('')  ;
    
      const enterHandler = (event)=>{
           setnewDetail(event.target.value);
           props.setnewName
      }

      return (
         
         <div className="detailFlex">
             <label className="Details">{props.detail}</label>
             <input className="EnterDetail" type="text" onChange={enterHandler} value={newdatail} ></input>
         </div>
         
      );
}

export default Input;
