import React , {useState} from "react";
import './input.css';

const Input = (props) =>{
    
      const [newDetal , setnewAge] = useState()

      const enterHandler = (event)=>{
           props.setnewName(event.target.value);
           props.setnewAge(event.target.value);
      }

      return (
         
         <div className="detailFlex">
             <label className="Details">{props.detail}</label>
             <input className="EnterDetail" type="text" onChange={enterHandler} value={props.newAge} ></input>
         </div>
         
      );
}

export default Input;
