//    const []
   const [getName , setNewfetchedName]=useState('');
   const [getAge , setNewfetchedAge] = useState('');
   const [isSubmit , setisSubmit] = useState(false);


   const newNameget = (fetchedName) =>{
      setNewfetchedName(fetchedName);
   }
   const newAgeget = (fetchedAge) =>{
      setNewfetchedAge(fetchedAge);
   }

   
    const submitHandler = (event)=>{
        event.preventDefault();
        // 

        const addDetail = {
            name:getName,
            age : getAge ,
            key : Math.round(Math.random()*100)
        }
        setisSubmit(true);
        props.takeDataForm(addDetail);
        setNewfetchedAge('');
        setNewfetchedName('');
        // console.log(getName , getAge , "submited form")
    }
    return (
       <form onSubmit={submitHandler}>
          <div className='FormContainer'>
          <div className="detailFlex">
             <label className="Details">Username</label>
             <input className="EnterDetail" type="text" onChange={nameHandler} value={newName} ></input>
             <label className="Details">Age {`(Years)`}</label>
             <input className="EnterDetail" type="Number" onChange={ageHandler} value={newAge} ></input>
             
         </div>
         
             <Button/>
          </div>
       </form>
    );
}

export default Form;
