import React from "react";
import Button from "../Button/Button";
import './InvalidInput.css';

const InvalidInput = () => {
    const clickHandler = () => {
        // Handle click event here
        console.log("Button clicked!"); // Example action
        
    }

    return (
        <div className="invalidState">
            <div className="upperState">Invalid Input</div>
            <div className="LowerState">Please enter a valid name and age (non-empty values).</div>
            <Button classUi={"bottomState"} buttonValue="Okay" onClick={clickHandler} />
        </div>
    )
}

export default InvalidInput;
