    lower={"Please enter a valid age (> 0)."}/> ) : checkneg  
  }

  { !isValid && !checkneg &&
     <div>
        <AddnewDetail sendObjectArray = {objectArray} /> 
    </div>
  } 
  { isValid &&
        <InvalidInput setcheckValid={setValid} upper={"Invalid Input"} lower={"Please enter a valid name and age (non-empty values)."}/>
  }


  {/*  */}
    </div>
  );
}

export default App;
